import React, { useState } from 'react';
import axios from 'axios';
import './FormStyleSheet.css';

const AddLecturerPage = ({ userRole }) => {
  const [lectureName, setLectureName] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await axios.post('http://localhost:5000/api/lecturers', {
        lectureName,
        username,
        password,
        role: "lecturer"
      });

      if (response.status === 201) {
        console.log('Lecturer added successfully');
        // Clear form fields after successful submission
        setLectureName('');
        setUsername('');
        setPassword('');
      } else {
        console.error('Failed to add lecturer');
      }
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  if (userRole !== 'admin') {
    return null; // If user is not admin, do not render the page
  }

  return (
    <div className='add_lec'>
      <h1>Add Lecturer</h1>
      <form onSubmit={handleSubmit}>
        <div className='input_box_lec'>
        <label htmlFor="lectureName">Name:</label>
        <input style={{height: '30px', borderRadius: '5px',marginLeft:'50px'}} 
          type="text" 
          id="lectureName" 
          name="lectureName" 
          value={lectureName}
          onChange={(e) => setLectureName(e.target.value)} 
          required />
        </div>
        
        <div className='input_box_lec'>
        <label htmlFor="username">Username:</label>
        <input style={{height: '30px', borderRadius: '5px',marginLeft:'20px'}} 
          type="text" 
          id="username" 
          name="username" 
          value={username}
          onChange={(e) => setUsername(e.target.value)} 
          required 
        />
        </div>
       <div className='input_box_lec'>
       <label htmlFor="password">Password:</label>
        <input style={{height: '30px', borderRadius: '5px',marginLeft:'23px'}} 
          type="password" 
          id="password" 
          name="password" 
          value={password}
          onChange={(e) => setPassword(e.target.value)} 
          required 
        />
       </div>
        
        <button style={{marginLeft:'200px',paddingTop:'5px'}} className='btn_academic' type="submit">Add Lecturer</button>
      </form>
    </div>
  );
};

export default AddLecturerPage;
